export default class Game extends Phaser.Scene
{
	private cursors!: Phaser.Types.Input.Keyboard.CursorKeys
	private jugador!: Phaser.Physics.Arcade.Sprite

	constructor()
	{
		super('game')
	}


	preload()
	{
		this.cursors = this.input.keyboard.createCursorKeys()
	}

	create()
	{
		const map = this.make.tilemap({ key: 'isla'})
		const tileset = map.addTilesetImage('isla', 'tiles', 16, 16)

		map.createStaticLayer('fondo', tileset)
		const wallsLayer = map.createStaticLayer('bordes', tileset)

		wallsLayer.setCollisionByProperty({colision: true})

		//debugDraw(wallsLayer, this)  //sirve para ver las colisiones

		this.jugador = this.physics.add.sprite(128, 128, 'jugador', 'adelante-1.png')
		this.jugador.setSize(this.jugador.width * 0.5, this.jugador.height * 0.9) //cambiar el tamaño de la hitbox
		this.jugador.setScale(this.jugador.width * 0.009, this.jugador.height * 0.009) //ajustar el tamaño del personaje


		this.anims.create({
			key: 'jugador-detenido',
			frames: [{key: 'jugador', frame: 'moviendose-adelante-1.png'}]
		})

		this.anims.create({
			key: 'moviendose-adelante',
			frames: this.anims.generateFrameNames('jugador', {start: 1, end: 8, prefix: 'moviendose-adelante-', suffix: '.png'}),
			repeat: -1,
			frameRate: 12
		})

		this.anims.create({
			key: 'moviendose-atras',
			frames: this.anims.generateFrameNames('jugador', {start: 1, end: 8, prefix: 'moviendose-atras-', suffix: '.png'}),
			repeat: -1,
			frameRate: 12
		})

		this.anims.create({
			key: 'moviendose-izquierda',
			frames: this.anims.generateFrameNames('jugador', {start: 1, end: 8, prefix: 'moviendose-izquierda-', suffix: '.png'}),
			repeat: -1,
			frameRate: 12
		})

		this.anims.create({
			key: 'moviendose-derecha',
			frames: this.anims.generateFrameNames('jugador', {start: 1, end: 8, prefix: 'moviendose-derecha-', suffix: '.png'}),
			repeat: -1,
			frameRate: 12
		})

		this.jugador.anims.play('jugador-detenido')

		this.physics.add.collider(this.jugador, wallsLayer)

		this.cameras.main.startFollow(this.jugador, true) //la camara sigue al jugador

	}

	update(t: number, dt: number)
	{
		if (!this.jugador || !this.jugador)
		{
			return
		}

		const speed = 100

		if (this.cursors.left?.isDown)
		{
			this.jugador.anims.play('moviendose-izquierda', true)
			this.jugador.setVelocity(-speed, 0)
		}
		else if (this.cursors.right?.isDown)
		{
			this.jugador.anims.play('moviendose-derecha', true)
			this.jugador.setVelocity(speed, 0)
		}
		else if (this.cursors.up?.isDown)
		{
			this.jugador.anims.play('moviendose-atras', true)
			this.jugador.setVelocity(0, -speed)
		}
		else if (this.cursors.down?.isDown)
		{
			this.jugador.anims.play('moviendose-adelante', true)
			this.jugador.setVelocity(0, speed)
		}
		else
		{
			const parts = this.jugador.anims.currentAnim.key.split('-')
			parts[1] = 'detenido'
			this.jugador.play(parts.join('-'))
			this.jugador.setVelocity(0, 0)
		}
	}
}
